	(491,12,'character_descriptions_0_character_name','Poseidon'),
	(492,12,'_character_descriptions_0_character_name','field_5599b01f321da'),
	(493,12,'character_descriptions_0_character_description','God of the sea. He has a great fondness for Troy and is sad to see it in ruins.'),
	(494,12,'_character_descriptions_0_character_description','field_5599b030321db'),
	(495,12,'character_descriptions_1_character_name','Menelaus'),
	(496,12,'_character_descriptions_1_character_name','field_5599b01f321da'),
	(497,12,'character_descriptions_1_character_description','King of Sparta. He has sacked Troy in the name of winning back his wife, Helen. He is very determined to appear above Helen’s charms, but fails.'),
	(498,12,'_character_descriptions_1_character_description','field_5599b030321db'),
	(499,12,'character_descriptions_2_character_name','Talthybius'),
	(500,12,'_character_descriptions_2_character_name','field_5599b01f321da'),
	(501,12,'character_descriptions_2_character_description','A Greek herald. He is always acting under orders from his masters, but it becomes clear that his conscience and a sense of pity for the Trojans interferes with his duties.'),
	(502,12,'_character_descriptions_2_character_description','field_5599b030321db'),
	(503,12,'character_descriptions_3_character_name','Soldiers'),
	(504,12,'_character_descriptions_3_character_name','field_5599b01f321da'),
	(505,12,'character_descriptions_3_character_description','Soldiers of the Greek army.'),
	(506,12,'_character_descriptions_3_character_description','field_5599b030321db'),
	(507,12,'character_descriptions_4_character_name','Pallas Athena'),
	(508,12,'_character_descriptions_4_character_name','field_5599b01f321da'),
	(509,12,'character_descriptions_4_character_description','Goddess of war. Partially responsible for the sacking of Troy, she now views the Greeks with contempt and seeks to punish them.'),
	(510,12,'_character_descriptions_4_character_description','field_5599b030321db'),
	(511,12,'character_descriptions_5_character_name','Hecuba'),
	(512,12,'_character_descriptions_5_character_name','field_5599b01f321da'),
	(513,12,'character_descriptions_5_character_description','Queen of Troy. She has lost her husband and children, and now must face the reality that she has fallen from queen to slave.'),
	(514,12,'_character_descriptions_5_character_description','field_5599b030321db'),
	(515,12,'character_descriptions_6_character_name','Helen'),
	(516,12,'_character_descriptions_6_character_name','field_5599b01f321da'),
	(517,12,'character_descriptions_6_character_description','Wife of Menelaus. Her beauty is her only form of power and she uses it not only to win men’s hearts, but to stay alive.'),
	(518,12,'_character_descriptions_6_character_description','field_5599b030321db'),
	(519,12,'character_descriptions_7_character_name','Andromache'),
	(520,12,'_character_descriptions_7_character_name','field_5599b01f321da'),
	(521,12,'character_descriptions_7_character_description','Wife of the Trojan Prince, Hector. Having been a good and obedient woman her whole life, she now faces the irony that these very qualities have made her the ideal concubine for a Greek general.'),
	(522,12,'_character_descriptions_7_character_description','field_5599b030321db'),
	(523,12,'character_descriptions_8_character_name','Cassandra'),
	(524,12,'_character_descriptions_8_character_name','field_5599b01f321da'),
	(525,12,'character_descriptions_8_character_description','Prophetess of Apollo and daughter of Hecuba. Driven mad by the god who gave her the gift of prophecy, Cassandra is joyful at the thought that her capture will bring ruin to the Greek king, Agamemnon.'),
	(526,12,'_character_descriptions_8_character_description','field_5599b030321db'),
	(527,12,'character_descriptions_9_character_name','Women'),
	(528,12,'_character_descriptions_9_character_name','field_5599b01f321da'),
	(529,12,'character_descriptions_9_character_description','The captive women of Troy.'),
	(530,12,'_character_descriptions_9_character_description','field_5599b030321db'),
