	(573,88,'_wp_attached_file','2014/04/rosen_guild_2014_cast.jpg'),
	(574,88,'_wp_attachment_metadata','a:5:{s:5:\"width\";i:2100;s:6:\"height\";i:855;s:4:\"file\";s:33:\"2014/04/rosen_guild_2014_cast.jpg\";s:5:\"sizes\";a:3:{s:9:\"thumbnail\";a:4:{s:4:\"file\";s:33:\"rosen_guild_2014_cast-150x150.jpg\";s:5:\"width\";i:150;s:6:\"height\";i:150;s:9:\"mime-type\";s:10:\"image/jpeg\";}s:6:\"medium\";a:4:{s:4:\"file\";s:33:\"rosen_guild_2014_cast-300x122.jpg\";s:5:\"width\";i:300;s:6:\"height\";i:122;s:9:\"mime-type\";s:10:\"image/jpeg\";}s:5:\"large\";a:4:{s:4:\"file\";s:34:\"rosen_guild_2014_cast-1024x417.jpg\";s:5:\"width\";i:1024;s:6:\"height\";i:417;s:9:\"mime-type\";s:10:\"image/jpeg\";}}s:10:\"image_meta\";a:11:{s:8:\"aperture\";d:3.100000000000000088817841970012523233890533447265625;s:6:\"credit\";s:0:\"\";s:6:\"camera\";s:12:\"COOLPIX L120\";s:7:\"caption\";s:0:\"\";s:17:\"created_timestamp\";i:1390858136;s:9:\"copyright\";s:0:\"\";s:12:\"focal_length\";s:3:\"4.5\";s:3:\"iso\";s:3:\"180\";s:13:\"shutter_speed\";s:15:\"0.0333333333333\";s:5:\"title\";s:0:\"\";s:11:\"orientation\";i:1;}}'),
